actionConfiguration.body:
async () => {
  try {
    const res = await LockConsignments.run({
      fleetTrackingIds: Table2.selectedRows.map(row => {
        return row.fleet_tracking_id;
      })
    });
    if (res.isError) {
      showAlert(res.message ?? "Something went wrong", "error");
      return;
    }
    showAlert("Successfully locked consignments", "success");
    return;
  } catch (err) {
    showAlert(err.message ?? "Something went wrong", "error");
    return;
  }
}

