actionConfiguration.body:
async () => {
  try {
    const fleetTrackingIds = (await get_fleetTrackingIds_of_invoic.run({
      invoiceId: readyInvoiceTbl.selectedRow.id
    })).map(row => {
      return row.fleet_tracking_id;
    });
    const res = await UnlockConsignments.run({
      fleetTrackingIds
    });
    if (res.isError) {
      showAlert(res.message ?? "Something went wrong", "error");
      return;
    }
    showAlert("Successfully unlocked consignments", "success");
    return;
  } catch (err) {
    showAlert(err.message ?? "Something went wrong", "error");
    return;
  }
}

