WITH
ins_trip_revenue_collection_log AS (UPDATE finance_trip_invoices
  SET received_amount = {{parseInt(alreadyCollectedVatAit.text)+parseInt(collected_net_to_redx.text)}}, last_user_data = {{appsmith.store.user_data}}, last_collection_medium={{collectionMediumSelect.selectedOptionLabel}},last_trx_id={{trxIdReceivedTxt.text}}, last_proof_type={{"'"+JSON.stringify(proofReceivedFilePicker.files)+"'"}},status='receiving-in-progress', updated_at=now()
  WHERE id = {{ readyInvoiceTbl.selectedRow.id }}
	RETURNING id, invoice_number)
	INSERT INTO invoice_collectable_received (
            received_amount, user_data, finance_trip_invoices_id, collection_medium, trx_id, proof_type, invoice_number
        )
    select {{collected_net_to_redx.text}}, {{appsmith.store.user_data}}, (select id from ins_trip_revenue_collection_log), {{collectionMediumSelect.selectedOptionLabel}}, {{trxIdReceivedTxt.text}}, {{"'"+JSON.stringify(proofReceivedFilePicker.files)+"'"}}, {{readyInvoiceTbl.selectedRow.invoice_number}}
