WITH
ins_trip_revenue_collection_log AS (UPDATE finance_trip_invoices
  SET received_amount = {{parseFloat(collected_net_to_redx.text) + parseFloat(readyInvoiceTbl.selectedRow.received_amount_wo_vat_ait)+ parseFloat(collected_ait.text) + parseFloat(readyInvoiceTbl.selectedRow.received_ait_amount) + parseFloat(collected_vat.text) + parseFloat(readyInvoiceTbl.selectedRow.received_vat_amount)}}, last_user_data = {{appsmith.store.user_data}}, last_collection_medium={{collectionMediumSelect.selectedOptionLabel}},last_trx_id={{trxIdReceivedTxt.text}}, last_proof_type={{"'"+JSON.stringify(proofReceivedFilePicker.files)+"'"}}, status= {{readyInvoiceTbl.selectedRow.total_price_after_vat_ait- readyInvoiceTbl.selectedRow.received_amount<=0?'receiving-complete':'receiving-in-progress'}} , updated_at=now(), received_vat_amount = {{parseFloat(collected_vat.text) + parseFloat(readyInvoiceTbl.selectedRow.received_vat_amount)}}, received_ait_amount ={{parseFloat(collected_ait.text) + parseFloat(readyInvoiceTbl.selectedRow.received_ait_amount)}}, vat_proof_type = {{vat_proof.files? "'"+JSON.stringify(vat_proof.files)+"'":NULL}},ait_proof_type = {{"'"+JSON.stringify(ait_proof.files)+"'"}}, 
	vat_collection_medium =	{{vatCollectionMediumSelect.selectedOptionLabel}}, ait_collection_medium =	{{aitCollectionMediumSelect.selectedOptionLabel}}, received_amount_wo_vat_ait =	{{parseFloat(collected_net_to_redx.text) + parseFloat(readyInvoiceTbl.selectedRow.received_amount_wo_vat_ait)}}											
  WHERE id = {{ readyInvoiceTbl.selectedRow.id }}
	RETURNING id, invoice_number)
	INSERT INTO invoice_collectable_received (
            received_amount, user_data, finance_trip_invoices_id, collection_medium, trx_id, proof_type, invoice_number, received_vat_amount, received_ait_amount, received_amount_wo_vat_ait, vat_proof_type, ait_proof_type, vat_collection_medium, ait_collection_medium
        )
    select {{parseFloat(collected_net_to_redx.text) + parseFloat(collected_ait.text) + parseFloat(collected_vat.text) }}, {{appsmith.store.user_data}}, (select id from ins_trip_revenue_collection_log), {{collectionMediumSelect.selectedOptionLabel}}, {{trxIdReceivedTxt.text}}, {{"'"+JSON.stringify(proofReceivedFilePicker.files)+"'"}}, {{readyInvoiceTbl.selectedRow.invoice_number}}, {{parseFloat(collected_vat.text)}}, {{parseFloat(collected_ait.text)}}, {{parseFloat(collected_net_to_redx.text)}}, {{"'"+JSON.stringify(vat_proof.files)+"'"}}, {{"'"+JSON.stringify(ait_proof.files)+"'"}},{{vatCollectionMediumSelect.selectedOptionLabel}},{{aitCollectionMediumSelect.selectedOptionLabel}}
