WITH
ins_trip_revenue_collection_log AS (UPDATE finance_trip_invoices
  SET received_amount = {{parseInt(collected_net_to_redx.text) + parseInt(alreadyCollectedWoVatAit.text)+ parseInt(collected_ait.text) + parseInt(alreadyCollectedAit.text) + parseInt(collected_vat.text) + parseInt(alreadyCollectedVat.text) }}, last_user_data = {{appsmith.store.user_data}}, last_collection_medium={{collectionMediumSelect.selectedOptionLabel}},last_trx_id={{trxIdReceivedTxt.text}}, last_proof_type={{"'"+JSON.stringify(proofReceivedFilePicker.files)+"'"}},status= {{readyInvoiceTbl.selectedRow.total_price_after_vat_ait- readyInvoiceTbl.selectedRow.received_amount<=0?'receiving-complete':'receiving-in-progress'}} , updated_at=now(), received_vat_amount = {{parseInt(collected_vat.text) + parseInt(alreadyCollectedVat.text)}}, received_ait_amount ={{parseInt(collected_ait.text) + parseInt(alreadyCollectedAit.text)}}, vat_proof_type = {{"'"+JSON.stringify(vat_proof.files)+"'"}},ait_proof_type = {{"'"+JSON.stringify(ait_proof.files)+"'"}}, 
	vat_collection_medium =	{{vatCollectionMediumSelect.selectedOptionLabel}}, ait_collection_medium =	{{aitCollectionMediumSelect.selectedOptionLabel}}, received_amount_wo_vat_ait =	{{parseInt(collected_net_to_redx.text) + parseInt(alreadyCollectedWoVatAit.text)}}											
  WHERE id = {{ readyInvoiceTbl.selectedRow.id }}
	RETURNING id, invoice_number)
	INSERT INTO invoice_collectable_received (
            received_amount, user_data, finance_trip_invoices_id, collection_medium, trx_id, proof_type, invoice_number
        )
    select {{parseInt(collected_net_to_redx.text) + parseInt(alreadyCollectedWoVatAit.text)+ parseInt(collected_ait.text) + parseInt(alreadyCollectedAit.text) + parseInt(collected_vat.text) + parseInt(alreadyCollectedVat.text)}}, {{appsmith.store.user_data}}, (select id from ins_trip_revenue_collection_log), {{collectionMediumSelect.selectedOptionLabel}}, {{trxIdReceivedTxt.text}}, {{"'"+JSON.stringify(proofReceivedFilePicker.files)+"'"}}, {{readyInvoiceTbl.selectedRow.invoice_number}}
